                                <svg class="absolute w-10 h-10 text-gray-400 -left-1" fill="currentColor" viewBox="0 0 20 20" xmlns="http://www.w3.org/2000/svg"><path fill-rule="evenodd" d="M10 9a3 3 0 100-6 3 3 0 000 6zm-7 9a7 7 0 1114 0H3z" clip-rule="evenodd"></path></svg>
                            </div>
                        </span>
                    </li>
                </ul>
            </div>
        </aside>
    </nav>
</template>

<script>
    import { computed, ref, onMounted } from 'vue';
    import { useRouter } from 'vue-router';
    import { initFlowbite } from 'flowbite';

    export default{
        props:[
            "navSize"
        ],
        setup(props){
            const router = useRouter()
            const toggled = ref(false)
            const theme = ref(true)

            let appTag = document.querySelector('#app')

            const nav_size = computed(() => {
                return props.navSize
            })

            const toggle = () => {
                toggled.value = !toggled.value
            }

            
            const toggleTheme = () => {
                theme.value = !theme.value
                if(theme.value){
                    appTag.classList.add('dark')
                } else {
                    appTag.classList.remove('dark')
                }
            }

            onMounted(() => {
                initFlowbite()
            })

            return { nav_size, toggle, toggleTheme, router, toggled, theme }
        }
    }

</script>

<style scoped>
    aside{
        grid-template-columns: 100%;
        grid-template-rows: 78% 22%;
        grid-template-areas: "nav" "profile";
    }
    .nav{
        grid-area: nav;
    }
    .profile{
        grid-area: profile;
    }
</style>